import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStream;
import java.io.InputStreamReader;
import java.io.OutputStream;
import java.io.PrintWriter;
import java.util.InputMismatchException;
import java.util.StringTokenizer;

public class Main {

	public static void main(String[] args) throws IOException {
		InputStream inputStream = System.in;
		OutputStream outputStream = System.out;
		InputReader in = new InputReader(inputStream);
		PrintWriter out = new PrintWriter(outputStream);
		TaskX solver = new TaskX();
		solver.solve(1, in, out);
		out.close();
	}

	static int INF = 1 << 30;

	static class TaskX {
		public void solve(int testNumber, InputReader in, PrintWriter out) {

			int n = in.nextInt();
			int k = in.nextInt();
			int[] wn = in.nextIntArray(n);

			int left = 0;
			int right = INF;
			int mid = 0;
			while (right > left + 1) {
				mid = (left + right) / 2;
				int count = 1;
				int ca = mid;
				for (int i = 0; i < n; i++) {
					if (ca - wn[i] >= 0) {
						ca -= wn[i];
					} else {
						ca = mid;
						count++;
						if (ca - wn[i] >= 0) {
							ca -= wn[i];
						} else {
							count = INF;
							break;
						}
					}
				}

				// k台以下で積める場合
				if (count <= k){
					right = mid;

				// ｋ台以上必要な場合
				} else if (count > k) {
					left = mid;
				}
			}

			out.println(right);

		}
	}

	static class InputReader {
		BufferedReader in;
		StringTokenizer tok;

		public String nextString() {
			while (!tok.hasMoreTokens()) {
				try {
					tok = new StringTokenizer(in.readLine(), " ");
				} catch (IOException e) {
					throw new InputMismatchException();
				}
			}
			return tok.nextToken();
		}

		public int nextInt() {
			return Integer.parseInt(nextString());
		}

		public long nextLong() {
			return Long.parseLong(nextString());
		}

		public double nextDouble() {
			return Double.parseDouble(nextString());
		}

		public int[] nextIntArray(int n) {
			int[] res = new int[n];
			for (int i = 0; i < n; i++) {
				res[i] = nextInt();
			}
			return res;
		}

		public long[] nextLongArray(int n) {
			long[] res = new long[n];
			for (int i = 0; i < n; i++) {
				res[i] = nextLong();
			}
			return res;
		}

		public InputReader(InputStream inputStream) {
			in = new BufferedReader(new InputStreamReader(inputStream));
			tok = new StringTokenizer("");
		}

	}

}

import java.util.Scanner;
public class Main {

	public static void main(String[] args) {
		Scanner stdIn = new Scanner(System.in);

		long h = stdIn.nextLong();
		long w = stdIn.nextLong();

		long cnt = 0;
		if(h==1 || w == 1) {
			System.out.println(1);
			return;
		}

		if(h % 2 == 0 && w % 2 == 0) { //h偶数、w偶数
			for(int i = 0; i < h; i++) {
				cnt += w/2;
			}
		}else if(h % 2 == 0 && w % 2 != 0) { //h偶数、w奇数
			for(int i = 0; i < h ; i++) {
				if(i % 2 == 0) {
					cnt += (w+1)/2;
				}else {
					cnt += w/2;
				}
			}
		}else if( h % 2 != 0 && w % 2 == 0) { // h奇数,w偶数
			for(int i = 0; i < h; i++) {
				cnt += w/2;
			}
		}else {                                //h奇数,w奇数
			for(int i = 0; i < h ; i++) {
				if(i % 2 == 0) {
					cnt += (w+1)/2;
				}else {
					cnt += w/2;
				}
			}
		}

		System.out.println(cnt);

	}

}

import java.util.Arrays;
import java.util.Scanner;

public class Main {
	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);
		int n = sc.nextInt();
		long a = sc.nextLong();
		long b = sc.nextLong();
		long[] h = new long[n];
		for(int i = 0; i < n; i++) h[i] = sc.nextLong();
		long l = 0, r = Integer.MAX_VALUE;
		while(r - l != 1) {
			long mid = (r + l) / 2;
			long[] x = Arrays.copyOf(h, h.length);
			long cnt = 0;
			for(int i = 0; i < x.length; i++) {
				if(x[i] - b * mid <= 0) continue;
				cnt += ((x[i] - b * mid) + (a - b - 1)) / (a - b);
			}
			if(mid >= cnt) r = mid;
			else l = mid;
		}
		System.out.println(r);
	}
}

