            if (Arrays.toString(arr).equals(Arrays.toString(P))) pNum = cnt;
            if (Arrays.toString(arr).equals(Arrays.toString(Q))) qNum = cnt;
            ++cnt;
        } while (nextPermutation(arr));

        System.out.println(Math.abs(pNum - qNum));

    }


