    static long comb(int n, int r)
    {
        long result = f[n];
        result = result * modInv(f[n - r]) % MOD;
        result = result * modInv(f[r]) % MOD;
        return result;
    }

    static long modInv(long n)
    {
        return modPow(n, MOD - 2);
    }

    static void factorial(int n)
    {
        f = new long[n + 1];
        f[0] = f[1] = 1;
        for (int i = 2; i <= n; i++)
        {
            f[i] = (f[i - 1] * i) % MOD;
        }
    }

    static long modPow(long x, long n)
    {
        long res = 1L;
        while (n > 0)
        {
            if ((n & 1) == 1)
            {
                res = res * x % MOD;
            }
            x = x * x % MOD;
            n >>= 1;
        }
        return res;
    }

