    void dfs(String s, int n) {

        if (s.length() == n) {
//            System.out.println(s);
            if (s.charAt(0) == '0') {
                boolean check = true;
                for (int i=0; i<s.length()-1; i++) {
                    if (!lists[s.charAt(i) - 48].contains(s.charAt(i+1) - 48)) {
                        check = false;
                        break;
                    }
                }
                if (check) cnt++;
            }

        } else {
            for (int i=0; i<n; i++) {
                if (s.contains(String.valueOf(i))) continue;
                dfs(s + i, n);
            }
        }
    }


