public class Main {
    static MyReader in = new MyReader();

    public static void main(String[] args) {
        int N = in.i();
        int k = ((int)Math.sqrt(1 + 8 * N) + 1) / 2;
        out.println(k * (k - 1) / 2 == N ? solve(k) : "No");
    }

    static String solve(int k) {
        StringBuilder sb = new StringBuilder("Yes\n");
        sb.append(k).append('\n');

        for (int i = 1; i < k; i++) {
            sb.append(k - 1).append(' ');

            int t = 1 + i * (i - 1) / 2;
            for (int j = 1; j < i; j++) {
                sb.append(t++).append(' ');
            }

            for (int j = i; j < k; j++) {
                sb.append(t).append(' ');
                t += j;
            }
            sb.append('\n');
        }

        sb.append(k - 1).append(' ');
        int t = 1;
        for (int i = 2; i <= k; i++) {
            sb.append(t).append(' ');
            t += i;
        }

        return sb.toString();
    }
}

class MyReader extends BufferedReader {
    char[] cbuf = new char[1024];
    int head = 0;
    int tail = 0;

    MyReader() {
        super(new InputStreamReader(System.in));
    }

    char next() {
        if (head == tail) {
            try {
                tail = super.read(cbuf, 0, cbuf.length);
            } catch (IOException e) {
                e.printStackTrace();
            }
            head = 0;
        }
        return cbuf[head++];
    }

    void back() {
        head--;
    }

    boolean minus() {
        boolean minus;
        while (true) {
            char c = next();
            if (!isDelimiter(c)) {
                if (!(minus = c == '-')) back();
                return minus;
            }
        }
    }

    void skip() {
        while (isDelimiter(next()));
        back();
    }

    char[] s(int N) {
        char[] cbuf = new char[N];
        read(cbuf, 0, N);
        return cbuf;
    }

    public int read(char[] cbuf, int off, int len) {
        skip();
        int i;
        for (i = 0; i < cbuf.length; i++) {
            char c = next();
            if (isDelimiter(c)) {
                break;
            }
            cbuf[i] = c;
        }
        return i;
    }

    boolean isDelimiter(char c) {
        return c == ' ' || c == '\n' || c == '\r';
    }

    int i() {
        boolean minus = minus();
        int n = 0;
        while (true) {
            int k = next() - '0';
            if (k < 0 || 9 < k) break;
            n = 10 * n + k;
        }
        return minus ? -n : n;
    }

    int[] ii(final int N) {
        int[] a = new int[N];
        for (int j = 0; j < a.length; j++) a[j] = i();
        return a;
    }

    long l() {
