    public void greedy3() {
        for (int i = 0; i < A; i++) score -= D * (D + 1) * c[i] / 2;
        int[] last = new int[A];
        Arrays.fill(last, -1);
        for (int i = 0; i + 3 < D; i += 4) {
            int max = 0;
