				max[i] = Math.min(max[i],t[i]);
			}
			if (nb != a[ni]) {
				max[ni] = Math.min(max[ni],a[ni]);
				min[ni] = Math.max(min[ni],a[ni]);
				nb = a[ni];
			}else {
				max[ni] = Math.min(max[ni],a[ni]);
			}

		}

		for (int i=0; i < n; i++) {
			//System.out.println(min[i] + ":" + max[i]);
			if (max[i] - min[i] < 0) {
				System.out.println(0);
				return;
			}
			ans *= max[i] - min[i] +1;
			ans %= C;
			//bi = bi.multiply(new BigInteger((max[i] - min[i] +1)+"" ));

		}


		System.out.println(ans);
	}


	public static void main(String[] args) {
		Main main = new Main();
		main.calc();

	}
