	static class TaskX {
		public void solve(int testNumber, InputReader in, PrintWriter out) {

			int n = in.nextInt();
			int k = in.nextInt();
			int l = in.nextInt();
			UnionFind u1 = new UnionFind(n);
			UnionFind u2 = new UnionFind(n);
			for (int i = 0; i < k; i++) {
				int p = in.nextInt()-1;
				int q = in.nextInt()-1;
				u1.link(p, q);
			}
			for (int i = 0; i < l; i++) {
				int r = in.nextInt()-1;
				int s = in.nextInt()-1;
				u2.link(r, s);
			}
			Map<Long, Integer> roots = new HashMap<Long, Integer>();
			for (int i = 0; i < n; i++) {
				long code = (long)u1.root(i)<< 32 | u2.root(i);
				roots.merge(code, 1, Integer::sum);
			}
			for (int i = 0; i < n; i++) {
				if (i > 0)out.print(" ");
				long code = (long)u1.root(i) << 32 | u2.root(i);
				out.print(roots.get(code));
			}

		}

		public class UnionFind {
			int[] data;

			public UnionFind(int n) {
				data = new int[n];
				Arrays.fill(data, -1);
			}

			boolean link(int x, int y) {
				x = root(x);
				y = root(y);
				if (x != y) {
					if (data[y] < data[x]) {
						data[y] += data[x];
						data[x] = y;
					} else {
						data[x] += data[y];
						data[y] = x;
					}
				}
				return x != y;
			}

			int root(int x) {
				return data[x] < 0 ? x : (data[x] = root(data[x]));
			}
		}
	}


