    public static void main(String[] args) {
        int N = in.i();
        int k = ((int)Math.sqrt(1 + 8 * N) + 1) / 2;
        out.println(k * (k - 1) / 2 == N ? solve(k) : "No");
    }

    static String solve(int k) {
        StringBuilder sb = new StringBuilder("Yes\n");
        sb.append(k).append('\n');

        for (int i = 1; i < k; i++) {
            sb.append(k - 1).append(' ');

            int t = 1 + i * (i - 1) / 2;
            for (int j = 1; j < i; j++) {
                sb.append(t++).append(' ');
            }

            for (int j = i; j < k; j++) {
                sb.append(t).append(' ');
                t += j;
            }
            sb.append('\n');
        }

        sb.append(k - 1).append(' ');
        int t = 1;
        for (int i = 2; i <= k; i++) {
            sb.append(t).append(' ');
            t += i;
        }

        return sb.toString();
    }
}

