  static class TaskA {
    public void solve(int testNumber, InputReader in, OutputWriter out) {
      char a = in.nextChar();
      char b = in.nextChar();
      if (a == 'H') {
        out.printLine(b);
      } else {
        out.printLine(b == 'H' ? 'D' : 'H');
      }
    }

