while n = gets.to_i
  break if n == 0
  root = ''
  @group = {}
  n.times do |i|
    g, m = gets.split(':')
    root = g if i == 0
    ms = m[0..-3].split(',')
    ms.each{ |m| @group[m] ||= []; @group[m] << g }
  end
  cnt = 0
  leaf = @group.keys
  @group.values.each{ |vs| vs.each{ |v| leaf.delete(v) } }
  leaf.each do |name|
    if find(name, root)
      cnt += 1
    end
  end
  p cnt
end

