    	int N = ni();

    	int[] T = new int[N];
    	int[] A = new int[N];

    	for (int i=0; i<N; i++) {
			T[i] = ni();
			A[i] = ni();
		}

    	long t = T[0];
    	long a = A[0];

    	for (int i=1; i<N; i++) {
