class UnionFind
  def initialize(size)
    @parent = (0...size).to_a
    @rank = Array.new(size, 0)
  end

  def find(x)
    return x if @parent[x] == x
    return (@parent[x] = find(@parent[x]))
  end

  def unite(x, y)
    x, y = find(x), find(y)
    return if x == y
    if @rank[x] < @rank[y]
      @parent[x] = y
    else
      @parent[y] = x
      @rank[x] += 1 if @rank[x] == @rank[y]
    end
  end

  def same(x, y)
    find(x) == find(y)
  end
end

