  private static void solve() {
    int N = ni();
    int C = ni();
    int[][] D = ntable(C, C);
    int[][] c = ntable(N, N);

    int[][] dp = new int[C][3];
    for (int i = 0; i < C; i++) {
      for (int x = 0; x < N; x++) {
        for (int y = 0; y < N; y++) {
          dp[i][(x + y) % 3] += D[c[x][y] - 1][i];
        }
      }
    }

    int min = Integer.MAX_VALUE;
    for (int i = 0; i < C; i++) {
      for (int j = 0; j < C; j++) {
        if (i == j)
