	static
	class UnionFind {
		private int[] data;
		private int[] next;
		private int[] last;

		public UnionFind(int size) {
			data = new int[size];
			next = new int[size];
			last = new int[size];
			clear();
		}

		public void clear() {
			Arrays.fill(data, -1);
			Arrays.fill(next, -1);
			for(int i = 0; i < last.length; i++) {
				last[i] = i;
			}
		}

		public int root(int x) { return data[x] < 0 ? x : (data[x] = root(data[x])); }

		public void union(int x, int y) {
			if((x = root(x)) != (y = root(y))) {
				if(data[y] < data[x]) { final int t = x; x = y; y = t; }
				data[x] += data[y];
				data[y] = x;
