        int array[] = new int[3], num = 0;
        int count = 0;

        while (count < 3) {
            num = sc.nextInt();
            if (0 < num && num < 10) {
                array[count] = num;
                count++;
            }
        }


        for (int i = 0; i < 3; i++) {
            for (int j = i + 1; j < 3; j++) {
                if (array[i] == array[j]) {
                    count = count - 1;
                }
            }
        }
