/**
 * Built using CHelper plug-in
 * Actual solution is at the top
 */
public class Main {
    public static void main(String[] args) {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        InputReader in = new InputReader(inputStream);
        OutputWriter out = new OutputWriter(outputStream);
        CTwoAlpinists solver = new CTwoAlpinists();
        solver.solve(1, in, out);
        out.close();
    }

    static class CTwoAlpinists {
        public void solve(int testNumber, InputReader in, OutputWriter out) {
            int n = in.readInt();
            long[] t = in.readLongArray(n);
            long[] a = in.readLongArray(n);

            long[] max = new long[n];
            long[] actual = new long[n];

            {
                for (int i = 0; i < n; i++) {
                    max[i] = Math.min(t[i], a[i]);
                }
            }

            {
                long prev = 0;
                for (int i = 0; i < n; i++) {
                    if (t[i] > prev) {
                        if (t[i] > max[i]) {
                            out.printLine(0);
                            return;
                        }
                        actual[i] = t[i];
                        prev = t[i];
                    }
                }
            }

            {
                long prev = 0;
                for (int i = n - 1; i >= 0; i--) {
                    if (a[i] > prev) {
                        if (a[i] > max[i]) {
                            out.printLine(0);
                            return;
                        }
                        actual[i] = a[i];
                        prev = a[i];
                    }
                }
            }

            long ans = 1;
            for (int i = 0; i < n; i++) {
                if (actual[i] != 0) continue;
                ans = (ans * max[i]) % MiscUtils.MOD7;
            }

            out.printLine(ans);
        }

    }

    static class InputReader {
        private InputStream stream;
        private byte[] buf = new byte[1024];
        private int curChar;
        private int numChars;
        private InputReader.SpaceCharFilter filter;

        public InputReader(InputStream stream) {
            this.stream = stream;
        }

        public long[] readLongArray(int size) {
            long[] array = new long[size];
            for (int i = 0; i < size; i++) {
                array[i] = readLong();
            }
            return array;
        }

        public int read() {
            if (numChars == -1) {
                throw new InputMismatchException();
            }
            if (curChar >= numChars) {
                curChar = 0;
                try {
                    numChars = stream.read(buf);
                } catch (IOException e) {
                    throw new InputMismatchException();
                }
                if (numChars <= 0) {
                    return -1;
                }
            }
            return buf[curChar++];
        }

        public int readInt() {
            int c = read();
            while (isSpaceChar(c)) {
                c = read();
            }
            int sgn = 1;
            if (c == '-') {
                sgn = -1;
                c = read();
            }
            int res = 0;
            do {
                if (c < '0' || c > '9') {
                    throw new InputMismatchException();
                }
                res *= 10;
                res += c - '0';
                c = read();
            } while (!isSpaceChar(c));
            return res * sgn;
        }

        public long readLong() {
            int c = read();
            while (isSpaceChar(c)) {
                c = read();
            }
            int sgn = 1;
            if (c == '-') {
                sgn = -1;
                c = read();
            }
            long res = 0;
            do {
                if (c < '0' || c > '9') {
                    throw new InputMismatchException();
                }
                res *= 10;
                res += c - '0';
                c = read();
            } while (!isSpaceChar(c));
            return res * sgn;
        }

        public boolean isSpaceChar(int c) {
            if (filter != null) {
                return filter.isSpaceChar(c);
            }
            return isWhitespace(c);
        }

        public static boolean isWhitespace(int c) {
            return c == ' ' || c == '\n' || c == '\r' || c == '\t' || c == -1;
        }

        public interface SpaceCharFilter {
            public boolean isSpaceChar(int ch);

        }

    }

    static class OutputWriter {
        private final PrintWriter writer;

        public OutputWriter(OutputStream outputStream) {
            writer = new PrintWriter(new BufferedWriter(new OutputStreamWriter(outputStream)));
        }

        public OutputWriter(Writer writer) {
            this.writer = new PrintWriter(writer);
        }

        public void close() {
            writer.close();
        }

        public void printLine(long i) {
            writer.println(i);
        }

        public void printLine(int i) {
            writer.println(i);
        }

    }

    static class MiscUtils {
        public static final int MOD7 = (int) (1e9 + 7);

    }
}

import java.io.*;
import java.util.*;

public class Main {
	static final Reader in = new Reader();
	static final long M=(long)1e9+7;

	public static void main(String[] args) {
		int n=in.nextInt();
		long[] ps = new long[n+1];
		long nf=1;
		for(int i=1; i<=n; ++i) {
			ps[i]=(ps[i-1]+in.nextInt())%M;
			nf=nf*i%M;
		}
		long ans=nf*ps[n]%M, s=0;
		for(int i=1; i<=n-1; ++i) {
			s=(s+ps[n-i]-ps[i]+M)%M;
			ans=(ans+nf*modI((long)(i+1)*(i>=n-1?1:i+2), M)%M*2*s+nf*modI(i+1, M)%M*(ps[i]+ps[n]-ps[n-i]+M))%M;
		}
		System.out.println(ans);
	}

	static long modI(long a, long m) {
		return (a%=m)<=1?1:(1-modI(m%a, a)*m)/a+m;
	}

	static class Reader {
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		StringTokenizer st;
		String next() {
			while(st==null||!st.hasMoreTokens()) {
				try {
					st = new StringTokenizer(br.readLine());
				} catch(Exception e) {}
			}
			return st.nextToken();
		}
		int nextInt() {
			return Integer.parseInt(next());
		}
	}
}

