        }
      }
	    void solve(IO io, PrintWriter out)
	    {
           int i, j;
          int n = io.nextInt();
          int m = io.nextInt();

          ArrayList<Pair> ar = new ArrayList<>();
          for(i=0 ; i<=n ; i++)
            	ar.add(new Pair(0,0));
