                ar.get(tmp).y++;
              else
                ar.get(tmp).x++;
            }
          }
          int pen=0, correct=0;
          for(Pair p : ar)
          {
            if(p.x!=0)
            {
              correct++;
              pen+=p.y;
            }
          }
            out.print(correct+" "+pen);
	    }

