        for (int i = 1; i < n; i++) {
            sumEastL[i] = sumEastL[i - 1] + (cs[i] == 'E' ? 1 : 0);
        }
        int min = 10000000;
        for (int i = 0; i < n; i++) {
            int num = sumEastL[n - 1] - sumEastL[i] + (i > 0 ? i - sumEastL[i - 1] : 0);
