    void dfs(String s, int n) {

        if (s.length() == n) {
//            System.out.println(s);
            if (s.charAt(0) == '0') {
                boolean check = true;
                for (int i=0; i<s.length()-1; i++) {
                    if (!lists[s.charAt(i) - 48].contains(s.charAt(i+1) - 48)) {
