			M[n-r+1][0] = 1;

			int[] v = new int[n-r+2];
			v[n-r+1] = 1;

			Result res = gaussElimination(M, v, mod);
			if(res.exists){
				long[][] ret = new long[r][];
				int p = 0;
				for(int i = 0;i < r;i++){
					ret[i] = new long[r-i];
					for(int j = 0;j < r-i;j++){
						ret[i][j] = res.sol[p++];
					}
				}
				return ret;
			}
		}
		return null;
	}

