import java.util.StringTokenizer;

public class Main {

	public static void main(String[] args) throws IOException {
		InputStream inputStream = System.in;
		OutputStream outputStream = System.out;
		InputReader in = new InputReader(inputStream);
		PrintWriter out = new PrintWriter(outputStream);
		TaskX solver = new TaskX();
		solver.solve(1, in, out);
		out.close();
	}

	static int INF = 1 << 30;
	static int modP = 1000000007;

	static class TaskX {
		public void solve(int testNumber, InputReader in, PrintWriter out) {

			int n = in.nextInt();
			long[] t = in.nextLongArray(n), a = in.nextLongArray(n);

			long[] tCalc = new long[n];
			Arrays.fill(tCalc, -1);

			long[] aCalc = new long[n];
			Arrays.fill(aCalc, -1);

			long[] tDef = new long[n];
			long[] aDef = new long[n];

			long tMax = -1, aMax = -1;
			for (int i = 0; i < n; i++) {
				if (tMax < t[i]) {
					tMax = t[i];
					tDef[i] = t[i];
				} else {
					tCalc[i] = tMax;
				}
			}
			for (int i = n-1; i >= 0; i--) {
				if (aMax < a[i]) {
					aMax = a[i];
					aDef[i] = a[i];
				} else {
					aCalc[i] = aMax;
				}
			}

			for (int i = 0; i < n; i++) {
				if (aCalc[i] == -1 && tCalc[i] == -1) {
					if (aDef[i] != tDef[i]) {
						out.println(0);
						return;
					}
				} else if (aCalc[i] == -1) {
					if (aDef[i] > tCalc[i]) {
						out.println(0);
						return;
					}
				} else if (tCalc[i] == -1) {
					if (tDef[i] > aCalc[i]) {
						out.println(0);
						return;
					}
				}
			}

			long ans = 1;
			for (int i = 0; i < n; i++) {
				if (tCalc[i] == -1 || aCalc[i] == -1) {
					continue;
				}
				ans = ans * min(aCalc[i],tCalc[i]) % modP;
			}
			out.println(ans);
		}
	}

	static class InputReader {
		BufferedReader in;
		StringTokenizer tok;

		public String nextString() {
			while (!tok.hasMoreTokens()) {
				try {
					tok = new StringTokenizer(in.readLine(), " ");
				} catch (IOException e) {
					throw new InputMismatchException();
				}
			}
			return tok.nextToken();
		}

		public int nextInt() {
			return Integer.parseInt(nextString());
		}

		public long nextLong() {
			return Long.parseLong(nextString());
		}

		public double nextDouble() {
			return Double.parseDouble(nextString());
		}

		public int[] nextIntArray(int n) {
			int[] res = new int[n];
			for (int i = 0; i < n; i++) {
				res[i] = nextInt();
			}
			return res;
		}

		public long[] nextLongArray(int n) {
			long[] res = new long[n];
			for (int i = 0; i < n; i++) {
				res[i] = nextLong();
			}
			return res;
		}

		public InputReader(InputStream inputStream) {
			in = new BufferedReader(new InputStreamReader(inputStream));
			tok = new StringTokenizer("");
		}

	}

}

import java.util.ArrayList;
import java.util.List;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner in = new Scanner(System.in);
        int n = in.nextInt();
        int m = in.nextInt();
        int a[][] = new int[n][n];

        for (int i = 0; i < m; i++) {
            int t1 = in.nextInt() - 1;
            int t2 = in.nextInt() - 1;
            a[t1][t2] = 1;
            a[t2][t1] = 1;
        }
        List<Integer> order = new ArrayList<>();
        order.add(0);
        List<Integer> remain = new ArrayList<>();
        for (int i = 1; i < n; i++) {
            remain.add(i);
        }
        System.out.println(f(order, remain, a));
    }

    static int f(List<Integer> order, List<Integer> remain, int edges[][]) {
        if (remain.size() == 0) {
            int cur = order.get(0);
            int next;
            for (int p = 1; p < order.size(); p++) {
                next = order.get(p);
                if (edges[cur][next] == 1 || edges[next][cur] == 1) {
                    cur = next; 
                } else {
                    return 0;
                }
            }
            return 1;
        }

        int total = 0;
        for (int p = 0; p < remain.size(); p++) {
            order.add(remain.get(p));
            remain.remove(p);
            total += f(order, remain, edges);
            remain.add(p, order.get(order.size() - 1));
            order.remove(order.size() - 1);
        }
        return total;
    }
}

