	static private int getPosi(int[] x) {

			int l = x.length;
			int p = 1;

			for(int i = 0; i < l; i++) {
				p += fact[l-i-1] * (x[i]-1);
				for(int j = i+1; j < l; j++) {
					if(x[j] > x[i])
						x[j]--;
				}
			}
		return p;

	}

}

