		W=sc.nextInt();
		visited=new boolean[H][W];
		ban=new int[H][W];
		String s;
		for(int i=0; i<H; i++){
			s=sc.next();
			for(int j=0; j<W; j++){
				if(s.charAt(j)=='#'){
					ban[i][j]=1;	//黒
				}
				else{
					ban[i][j]=0;	//白
				}
			}
		}

		for(int i=0; i<H; i++){		//しらべる
			for(int j=0; j<W; j++){
				if(ban[i][j]==1 && !visited[i][j]){
					kuro=0; siro=0;
					dfs(i,j);
					sum+=kuro*siro;
				}
			}
		}
		pl(sum);
	}

	public static void dfs(int y,int x){
		int[] vx={0,1,0,-1};
		int[] vy={-1,0,1,0};
		if(ban[y][x]==1){
			kuro++;
		}
		else{
			siro++;
		}
		visited[y][x]=true;
		for(int i=0; i<4; i++){
			if(x+vx[i]>=0 && x+vx[i]<W && y+vy[i]>=0 && y+vy[i]<H){
				if(ban[y][x]!=ban[y+vy[i]][x+vx[i]] && !visited[y+vy[i]][x+vx[i]]){
					dfs(y+vy[i],x+vx[i]);
				}
			}
		}
	}
