class Main {

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[] a = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextInt();
        }
        int[] b = new int[n];
        for (int i = 0; i < n; i++) {
            b[i] = sc.nextInt();
        }
        int[] c = new int[n];
        for (int i = 0; i < n; i++) {
            c[i] = sc.nextInt();
        }
        Arrays.sort(a);
        Arrays.sort(b);
        Arrays.sort(c);
        System.out.println(solve(a, b, c));
    }

    private static long solve(int[] a, int[] b, int[] c) {
        int n = a.length;
        long[] bmemo = new long[n];
        int cidx = 0;
        for (int j = 0; j < n; j++) {
            for (int k = cidx; k < n; k++) {
                if (b[j] >= c[k]) {
                    cidx++;
                } else {
                    bmemo[j] = n - k;
                    break;
                }
            }
        }
        for (int i = n - 2; i >= 0; i--) {
            bmemo[i] += bmemo[i + 1];
        }
        long sum = 0;
        int bidx = 0;
        for (int i = 0; i < n; i++) {
            for (int j = bidx; j < n; j++) {
                if (a[i] >= b[j]) {
                    bidx++;
                } else {
                    sum += bmemo[j];
                    break;
                }
            }
        }
        return sum;
    }
}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main {
    public static void main(String[] args) throws IOException {
        BufferedReader reader = new BufferedReader(new InputStreamReader(System.in));
        int count = Integer.valueOf(reader.readLine());
        String s = reader.readLine();

        long[][] dp = new long[count][count];
        dp[0][0] = 1;
        long modulo = 1_000_000_007;

        for (int i = 1; i < count; ++i) {
            if (s.charAt(i - 1) == '<') {
                dp[i][0] = 0;
                for (int j = 1; j <= i; ++j) {
                    dp[i][j] = dp[i][j - 1] + dp[i - 1][j - 1];
                    dp[i][j] %= modulo;
                }
            } else {
                dp[i][i] = 0;
                for (int j = i - 1; j >= 0; --j) {
                    dp[i][j] = dp[i][j + 1] + dp[i - 1][j];
                    dp[i][j] %= modulo;
                }
            }
        }

        long res = 0;
        for (int i = 0; i < count; ++i) {
            res += dp[count - 1][i];
            res %= modulo;
        }
        System.out.println(res);
    }
}

