            if(sum + ws[i] <= p) {
            sum += ws[i];
            } else {
                count++;
                if(ws[i] <= p){
                sum = ws[i];
                } else{
                    count += 99999;
                }
            }
        }
        return count;
    }
    static long solve(long p, int[] ws, long k){
        long right = p;
        long left = 0;
        long mid;
        while(right - left > 1) {
            mid = (left + right) / 2;
            int v = searchP(mid,ws);
            if(k >= v) {
                right = mid;
            } else {
               left = mid;

            }
        }
    return right;
    }
