public class Main {

  static int mod = 1000003;
  static int[][] fif = enumFIF(mod * 3, mod);

  private static void solve() {
    int q = ni();

    for (int i = 0; i < q; i++) {
      int x = ni();
      int d = ni();
      int n = ni();
      out.println(f(x, d, n));
    }
  }

  private static long f(int x, int d, int n) {
    if (x == 0) {
      return 0;
    } else if (d == 0) {
      return pow(x, n, mod);
    }

    int y = (int) (x * invl(d, mod) % mod);
    if (n >= mod || y % mod > (y + n - 1) % mod) {
      return 0;
    }

    long z = fif[0][y + n - 1] * invl(fif[0][y - 1], mod) % mod;
    z *= pow(d, n, mod);
    z %= mod;

    return z;
  }

  public static long pow(long a, long n, long mod) {
    // a %= mod;
    long ret = 1;
    int x = 63 - Long.numberOfLeadingZeros(n);
    for (; x >= 0; x--) {
      ret = ret * ret % mod;
      if (n << 63 - x < 0)
        ret = ret * a % mod;
    }
    return ret;
  }


  public static int[][] enumFIF(int n, int mod) {
    int[] f = new int[n + 1];
    int[] invf = new int[n + 1];
    f[0] = 1;
    for (int i = 1; i <= n; i++) {
      f[i] = (int) ((long) f[i - 1] * i % mod);
    }
    long a = f[n];
    long b = mod;
    long p = 1, q = 0;
    while (b > 0) {
      long c = a / b;
      long d;
      d = a;
      a = b;
      b = d % b;
      d = p;
      p = q;
      q = d - c * q;
    }
    invf[n] = (int) (p < 0 ? p + mod : p);
    for (int i = n - 1; i >= 0; i--) {
      invf[i] = (int) ((long) invf[i + 1] * (i + 1) % mod);
    }
    return new int[][] {f, invf};
  }

  public static long invl(long a, long mod) {
    long b = mod;
    long p = 1, q = 0;
    while (b > 0) {
      long c = a / b;
      long d;
      d = a;
      a = b;
      b = d % b;
      d = p;
      p = q;
      q = d - c * q;
    }
    return p < 0 ? p + mod : p;
  }


