public final class Main {
    public static void main(String[] args) throws Exception {
        try (var input = new Reader()) {
            int k = input.nextInt();
            int a = input.nextInt();
          	int b = input.nextInt();
          	for (int i = a; i <= b; ++i) {
              	if (i % k == 0) {
                  out.printf("OK%n");
                  return;
                }
            }
          	out.printf("NG%n");
        }
    }

    private static final class Reader implements AutoCloseable {
        private final int BUFFER_SIZE = 1 << 16;
        private DataInputStream din;
        private FileInputStream fis;
        private byte[] buffer;
        private int bufferPointer, bytesRead;

        private Reader() {
            din = new DataInputStream(System.in);
            buffer = new byte[BUFFER_SIZE];
            bufferPointer = bytesRead = 0;
        }

        private Reader(String filename) throws IOException {
            fis = new FileInputStream(filename);
            din = new DataInputStream(fis);
            buffer = new byte[BUFFER_SIZE];
            bufferPointer = bytesRead = 0;
        }

        private String readLine() throws IOException {
            byte[] buf = new byte[64]; // line length
            int cnt = 0, c;
            while ((c = read()) != -1) {
                if (c == '\n') { break; }
                buf[cnt++] = (byte) c;
            }
            return new String(buf, 0, cnt);
        }

        private int nextInt() throws IOException {
            int ret = 0;
            byte c = read();
            while (c <= ' ') { c = read(); }
            boolean neg = (c == '-');
            if (neg)
                c = read();
            do {
                ret = ret * 10 + c - '0';
            } while ((c = read()) >= '0' && c <= '9');

            if (neg) { return -ret; }
            return ret;
        }

        private long nextLong() throws IOException {
            long ret = 0;
            byte c = read();
            while (c <= ' ') { c = read(); }
            boolean neg = (c == '-');
            if (neg)
                c = read();
            do {
                ret = ret * 10 + c - '0';
            }
            while ((c = read()) >= '0' && c <= '9');
            if (neg) { return -ret; }
            return ret;
        }

        private double nextDouble() throws IOException {
            double ret = 0, div = 1;
            byte c = read();
            while (c <= ' ') { c = read(); }
            boolean neg = (c == '-');
            if (neg) { c = read(); }
            do {
                ret = ret * 10 + c - '0';
            }
            while ((c = read()) >= '0' && c <= '9');

            if (c == '.') {
                while ((c = read()) >= '0' && c <= '9') {
                    ret += (c - '0') / (div *= 10);
                }
            }

            if (neg) { return -ret; }
            return ret;
        }

        private void fillBuffer() throws IOException {
            bytesRead = din.read(buffer, bufferPointer = 0, BUFFER_SIZE);
            if (bytesRead == -1) { buffer[0] = -1; }
        }

        private byte read() throws IOException {
            if (bufferPointer == bytesRead) { fillBuffer(); }
            return buffer[bufferPointer++];
        }

        @Override
        public void close() throws Exception {
            if (fis != null) {
                fis.close();
            }

            if (din != null) {
                din.close();
            }
        }
    }
}

import java.io.FileNotFoundException;
import java.util.Arrays;
import java.util.Scanner;

public class Main {
	public static void main(String[] args) throws FileNotFoundException {
		new Main().run();
	}

	void run() {
		Scanner sc = new Scanner(System.in);
		int n = sc.nextInt();
		int[] a = new int[n];
		for (int i = 0; i < n; ++i) {
			a[i] = sc.nextInt();
		}
		MaxHeapSort maxheap = new MaxHeapSort(new int[] {});
		for (int i = 0; i < n; ++i) {
			maxheap.add(a[i]);
		}
		a = maxheap.sortedArray();
		for (int i = 0; i < n; ++i) {
			System.out.print(a[i] + (i == n - 1 ? "\n" : " "));
		}
	}

	class MaxHeapSort {
		int n;
		int[] v;

		public MaxHeapSort(int[] v_) {
			if (v_.length > 1_000_000)
				throw new AssertionError();
			v = Arrays.copyOf(v_, 1_000_000);
			n = v_.length;
			for (int i = n; i >= 0; --i) {
				maxHeapify(i);
			}
		}

		void add(int val) {
			v[n] = val;
			int cur = n;
			++n;
			while (cur > 0) {
				cur = (cur - 1) / 2;
				maxHeapify(cur);
			}
		}

		int[] sortedArray() {
			int sz = n;
			int[] ret = new int[sz];
			for (int i = 0; i < sz; ++i) {
				ret[sz - 1 - i] = poll();
			}
			return ret;
		}

		int poll() {
			int ret = v[0];
			v[0] = v[n - 1];
			v[n - 1] = ret;
			--n;
			maxHeapify(0);
			return ret;
		}

		void maxHeapify(int cur) {
			if (2 * cur + 1 >= n)
				return;
			if (v[cur] >= v[2 * cur + 1] && (2 * cur + 2 >= n || v[cur] >= v[2 * cur + 2])) {
				return;
			}
			int dst = -1;
			if (2 * cur + 2 >= n || v[2 * cur + 1] >= v[2 * cur + 2]) {
				dst = 2 * cur + 1;
			} else {
				dst = 2 * cur + 2;
			}
			int tmp = v[cur];
			v[cur] = v[dst];
			v[dst] = tmp;
			maxHeapify(dst);
		}
	}

	static void tr(Object... objects) {
		System.out.println(Arrays.deepToString(objects));
	}
}

