  end
end

class Dijkstra
  def initialize(n)
    @n = n
    @adj = Array.new(n) { Array.new(0) }
  end

  def set(s, t, cost)
    @adj[s].push({ to: t, cost: cost })
  end

  def calc(s)
    @visited = Array.new(@n, -1)
    que = PriorityQueue.new
    que.push(St.new(s, 0))
    while que.length > 0
      current = que.pop
      cp = current.p
      next if (@visited[cp] != -1)
      @visited[cp] = current.cost
      @adj[cp].each do |a|
        if (@visited[a[:to]] == -1)
          que.push(St.new(a[:to], current.cost + a[:cost]))
        end
      end
    end
  end

  def result(p)
    @visited[p]
  end
end

