  static final long INF = Long.MAX_VALUE / 2;

  public static class Solver {
    public Solver() {

      int Q = sc.nextInt();

      for (int q=0; q<Q; q++) {
        long A = sc.nextLong();
        long B = sc.nextLong();
        long P = A * B;

        long sqrt = sqrt(P-1);

        long ans = 2*sqrt;

        if (Math.min(A, B) <= sqrt) ans--; //高橋君と同じ順位になっているものを除く
        if (sqrt * (sqrt+1) > P-1) ans--; //ダブルカウントしているものを除く(x == y のとき)

        out.println(ans);
      }


    }

    long sqrt(long n) {
      long res = (long)Math.floor(Math.sqrt(n));
      while (res * res > n) res--;
      while ((res+1) * (res+1) <= n) res++;
      return res;
    }

  }

  public static void main(String[] args) {
    new Solver();
    out.flush();
  }

