/**
 * Built using CHelper plug-in
 * Actual solution is at the top
 */
public class Main {
    public static void main(String[] args) {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        InputReader in = new InputReader(inputStream);
        OutputWriter out = new OutputWriter(outputStream);
        CTwoAlpinists solver = new CTwoAlpinists();
        solver.solve(1, in, out);
        out.close();
    }

    static class CTwoAlpinists {
        public void solve(int testNumber, InputReader in, OutputWriter out) {
            int n = in.readInt();
            long[] t = in.readLongArray(n);
            long[] a = in.readLongArray(n);

            long[] max = new long[n];
            long[] actual = new long[n];

            {
                for (int i = 0; i < n; i++) {
                    max[i] = Math.min(t[i], a[i]);
                }
            }

            {
                long prev = 0;
                for (int i = 0; i < n; i++) {
                    if (t[i] > prev) {
                        if (t[i] > max[i]) {
                            out.printLine(0);
                            return;
                        }
                        actual[i] = t[i];
                        prev = t[i];
                    }
                }
            }

            {
                long prev = 0;
                for (int i = n - 1; i >= 0; i--) {
                    if (a[i] > prev) {
                        if (a[i] > max[i]) {
                            out.printLine(0);
                            return;
                        }
                        actual[i] = a[i];
                        prev = a[i];
                    }
                }
            }

            long ans = 1;
            for (int i = 0; i < n; i++) {
                if (actual[i] != 0) continue;
                ans = (ans * max[i]) % MiscUtils.MOD7;
            }

            out.printLine(ans);
        }

    }

    static class InputReader {
        private InputStream stream;
        private byte[] buf = new byte[1024];
        private int curChar;
        private int numChars;
        private InputReader.SpaceCharFilter filter;

        public InputReader(InputStream stream) {
            this.stream = stream;
        }

        public long[] readLongArray(int size) {
            long[] array = new long[size];
            for (int i = 0; i < size; i++) {
                array[i] = readLong();
            }
            return array;
        }

        public int read() {
            if (numChars == -1) {
                throw new InputMismatchException();
            }
            if (curChar >= numChars) {
                curChar = 0;
                try {
                    numChars = stream.read(buf);
                } catch (IOException e) {
                    throw new InputMismatchException();
                }
                if (numChars <= 0) {
                    return -1;
                }
            }
            return buf[curChar++];
        }

        public int readInt() {
            int c = read();
            while (isSpaceChar(c)) {
                c = read();
            }
            int sgn = 1;
            if (c == '-') {
                sgn = -1;
                c = read();
            }
            int res = 0;
            do {
                if (c < '0' || c > '9') {
                    throw new InputMismatchException();
                }
                res *= 10;
                res += c - '0';
                c = read();
            } while (!isSpaceChar(c));
            return res * sgn;
        }

        public long readLong() {
            int c = read();
            while (isSpaceChar(c)) {
                c = read();
            }
            int sgn = 1;
            if (c == '-') {
                sgn = -1;
                c = read();
            }
            long res = 0;
            do {
                if (c < '0' || c > '9') {
                    throw new InputMismatchException();
                }
                res *= 10;
                res += c - '0';
                c = read();
            } while (!isSpaceChar(c));
            return res * sgn;
        }

        public boolean isSpaceChar(int c) {
            if (filter != null) {
                return filter.isSpaceChar(c);
            }
            return isWhitespace(c);
        }

        public static boolean isWhitespace(int c) {
            return c == ' ' || c == '\n' || c == '\r' || c == '\t' || c == -1;
        }

        public interface SpaceCharFilter {
            public boolean isSpaceChar(int ch);

        }

    }

    static class OutputWriter {
        private final PrintWriter writer;

        public OutputWriter(OutputStream outputStream) {
            writer = new PrintWriter(new BufferedWriter(new OutputStreamWriter(outputStream)));
        }

        public OutputWriter(Writer writer) {
            this.writer = new PrintWriter(writer);
        }

        public void close() {
            writer.close();
        }

        public void printLine(long i) {
            writer.println(i);
        }

        public void printLine(int i) {
            writer.println(i);
        }

    }

    static class MiscUtils {
        public static final int MOD7 = (int) (1e9 + 7);

    }
}

import java.io.FileNotFoundException;
import java.util.Arrays;
import java.util.Scanner;

public class Main {
	public static void main(String[] args) throws FileNotFoundException {
		new Main().run();
	}

	void run() {
		Scanner sc = new Scanner(System.in);
		int n = sc.nextInt();
		int[] a = new int[n];
		for (int i = 0; i < n; ++i) {
			a[i] = sc.nextInt();
		}
		MaxHeapSort maxheap = new MaxHeapSort(new int[] {});
		for (int i = 0; i < n; ++i) {
			maxheap.add(a[i]);
		}
		a = maxheap.sortedArray();
		for (int i = 0; i < n; ++i) {
			System.out.print(a[i] + (i == n - 1 ? "\n" : " "));
		}
	}

	class MaxHeapSort {
		int n;
		int[] v;

		public MaxHeapSort(int[] v_) {
			if (v_.length > 1_000_000)
				throw new AssertionError();
			v = Arrays.copyOf(v_, 1_000_000);
			n = v_.length;
			for (int i = n; i >= 0; --i) {
				maxHeapify(i);
			}
		}

		void add(int val) {
			v[n] = val;
			int cur = n;
			++n;
			while (cur > 0) {
				cur = (cur - 1) / 2;
				maxHeapify(cur);
			}
		}

		int[] sortedArray() {
			int sz = n;
			int[] ret = new int[sz];
			for (int i = 0; i < sz; ++i) {
				ret[sz - 1 - i] = poll();
			}
			return ret;
		}

		int poll() {
			int ret = v[0];
			v[0] = v[n - 1];
			v[n - 1] = ret;
			--n;
			maxHeapify(0);
			return ret;
		}

		void maxHeapify(int cur) {
			if (2 * cur + 1 >= n)
				return;
			if (v[cur] >= v[2 * cur + 1] && (2 * cur + 2 >= n || v[cur] >= v[2 * cur + 2])) {
				return;
			}
			int dst = -1;
			if (2 * cur + 2 >= n || v[2 * cur + 1] >= v[2 * cur + 2]) {
				dst = 2 * cur + 1;
			} else {
				dst = 2 * cur + 2;
			}
			int tmp = v[cur];
			v[cur] = v[dst];
			v[dst] = tmp;
			maxHeapify(dst);
		}
	}

	static void tr(Object... objects) {
		System.out.println(Arrays.deepToString(objects));
	}
}

