					}

				}
				else{
					if(field[i][0]==-1){

					}
					else{
						if(!dp[i-1][0].equals("0")&&!dp[i-1][0].equals("")){
							dp[i][0]=dp[i-1][0]+field[i][0];
						}
						else{
							dp[i][0]=""+field[i][0];
						}
					}
				}
			}
			String ans="0";
			for(int i = 1;i<h;i++){
