import java.util.Scanner;

public class Main {

	static int[] fact = new int[9];


	public static void main(String[] args) {
		fact[1] = 1;
		for(int i = 2; i <= 8; i++)
			fact[i] = fact[i-1] * i;

		Scanner sc = new Scanner(System.in);
		int n = sc.nextInt();
		int[] p = new int[n];
		for(int i = 0; i < n; i++)
			p[i] = sc.nextInt();

		int[] q = new int[n];
		for(int i = 0; i < n; i++)
			q[i] = sc.nextInt();

		int ans = Math.abs(getPosi(p) - getPosi(q));
		System.out.println(ans);
        return;
    }

	static private int getPosi(int[] x) {

			int l = x.length;
			int p = 1;

			for(int i = 0; i < l; i++) {
				p += fact[l-i-1] * (x[i]-1);
				for(int j = i+1; j < l; j++) {
					if(x[j] > x[i])
						x[j]--;
				}
			}
		return p;

	}

}

import java.io.BufferedReader;
import java.io.InputStreamReader;
import java.util.*;
import java.util.stream.Collectors;

public class Main {
    public static void main(String[] args) {
        Main main = new Main();
        main.solve();
    }

    public void solve() {
        Scanner scan = new Scanner(new BufferedReader(new InputStreamReader(System.in)));
        int N = scan.nextInt();
        List<Integer> candidate = new ArrayList<>();
        for (int i = 1; i <= N; i++) {
            candidate.add(i);
        }
        scan.nextLine();
        String p = scan.nextLine();
        String q = scan.nextLine();
        List<List<Integer>> list = make(candidate);
        List<String> strings = new ArrayList();
        for (List<Integer> x : list) {
            strings.add(x.stream().map(i -> i.toString()).collect(Collectors.joining(" ")));
        }
        int len = strings.size();
        int l = 0;
        int r = 0;
        for (int i = 0; i < len; i++) {
            if (p.equals(strings.get(i))) {
                l = i;
            }
            if (q.equals(strings.get(i))) {
                r = i;
            }
        }
        System.out.println(Math.abs(l - r));
    }

    public static List<List<Integer>> make(List<Integer> candidate) {
        if (candidate.size() == 0) {
            List<List<Integer>> empty = new ArrayList<>();
            empty.add(new ArrayList<>());
            return empty;
        }

        return candidate.stream().flatMap(i -> {
            List<Integer> rest = new ArrayList<>(candidate);
            rest.remove(i);
            return make(rest).stream().map(list -> {
                list.add(0, i);
                return list;
            });
        }).collect(Collectors.toList());
    }
}

