    public static void main(String args[]) {
	Scanner sc = new Scanner(System.in);

	while (true) {
	    int n = sc.nextInt();
	    int ans = 0;
	    if (n == 0) {
		break;
	    }

	    ans = solve(n);
	    System.out.println(ans);
	}
