	public static void main(String[] args) {
		new Main().start();
		System.exit(0);
	}

	public void start() {
		while (true) {
			try {
				this.inputData = reader.readLine();
			} catch (IOException e) {
				e.printStackTrace();
			}

			this.data = this.inputData.split(" ");

			if (this.data.length == 1) {
				if (this.data[0].equals("0")) {
					break;
				}
				try {
					this.num = Integer.parseInt(this.data[0]);
				} catch (Exception e) {
					continue;
				}

			}

			this.humanData.clear();

			for (int i = 0; i < this.num; i++) {
				try {
					this.inputData = reader.readLine();
				} catch (IOException e) {
					e.printStackTrace();
				}
				this.data = this.inputData.split(" ");

				this.humanData.put(Integer.parseInt(data[0]),
						Integer.parseInt(data[1]) + Integer.parseInt(data[2]));

			}

			Iterator<Integer> itId = this.humanData.keySet().iterator();
			int numCount = 0;
			while (itId.hasNext()) {
				int id = itId.next();
				if (numCount == 0) {
					this.topId.add(id);
					this.topLength.add(this.humanData.get(id));
				} else {
					if (this.topLength.get(count) <= this.humanData.get(id)) {
						this.topId.set(this.count, id);
						this.topLength.set(this.count, this.humanData.get(id));
					}
				}
				numCount++;
			}

			this.count++;

		}

		for (int i = 0; i < this.topId.size(); i++) {
			System.out.println(this.topId.get(i) + " " + this.topLength.get(i));
		}

	}

}

import java.util.*;
public class Main {
	int n;
	int[] num;
	Scanner stdin =new Scanner(System.in);

	void Solve(){
		num=new int[21000000];
		while(true){
			n=stdin.nextInt();
			if(n==0) break;
			System.out.println(findPrime(n+1,2*n));
		}

	}
	int findPrime(int a,int b){
		int count=0;
		for(int i=a;i<=b;i++){
			if(judgePrime(i)) count++;
		}

		return count;
	}

	boolean judgePrime(int p){
		if(num[p]==1) return true;
		if(num[p]==-1) return false;

		if(p<4){
			num[p]=1;
			return true;
		}
		else{
			if(p%2==0){
				num[p]=-1;
				return false; 
			}

			for(int i=3;i<p;i++){
				if(p%i==0){
					num[p]=-1;
					return false;
				}
				i++;
			}
			num[p]=1;
			return true;
		}
	}



	public static void main(String args[]){
		Main m=new Main();
		m.Solve();
	}

}

