	class A{
		long euclid(long a, long b){
		    long temp;

		    if(a < b) { 
		    	temp = a;
		    	a = b;
		    	b = temp;
		    }
		    if(a % b == 0) return b;
		    return euclid( b, a % b );
		}
		void doIt(){
			int n = sc.nextInt();
			long A = 1;
			long B = 1;
			for(int i = 0;i < n;i++){
