import java.io.OutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.PrintWriter;
import java.io.Closeable;
import java.io.IOException;
import java.util.NoSuchElementException;
import java.io.InputStream;

/**
 * Built using CHelper plug-in
 * Actual solution is at the top
 *
 * @author HBonsai
 */
public class Main {
    public static void main(String[] args) {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        FastScanner in = new FastScanner(inputStream);
        PrintWriter out = new PrintWriter(outputStream);
        CMultipleClocks solver = new CMultipleClocks();
        solver.solve(1, in, out);
        out.close();
    }

    static class CMultipleClocks {
        public void solve(int testNumber, FastScanner in, PrintWriter out) {
            int N = in.nextInt();
            long[] T = in.nextLongArray(N);
            long ans = T[0];
            for (int i = 1; i < N; i++) {
                long gcd = gcd(ans, T[i]);
                ans = T[i] * (ans / gcd);
            }
            out.println(ans);
        }

        private static long gcd(long m, long n) {
            if (m < n) return gcd(n, m);
            if (n == 0) return m;
            return gcd(n, m % n);
        }

    }

    static class FastScanner implements Closeable {
        private final InputStream in;
        private final byte[] buffer = new byte[1024];
        private int ptr = 0;
        private int buflen = 0;

        public FastScanner(InputStream in) {
            this.in = in;
        }

        private boolean hasNextByte() {
            if (ptr < buflen) {
                return true;
            } else {
                ptr = 0;
                try {
                    buflen = in.read(buffer);
                } catch (IOException e) {
                    e.printStackTrace();
                }
                if (buflen <= 0) {
                    return false;
                }
            }
            return true;
        }

        private int readByte() {
            if (hasNextByte()) return buffer[ptr++];
            else return -1;
        }

        private static boolean isPrintableChar(int c) {
            return 33 <= c && c <= 126;
        }

        public boolean hasNext() {
            while (hasNextByte() && !isPrintableChar(buffer[ptr])) ptr++;
            return hasNextByte();
        }

        public long nextLong() {
            if (!hasNext()) throw new NoSuchElementException();
            long n = 0;
            boolean minus = false;
            int b = readByte();
            if (b == '-') {
                minus = true;
                b = readByte();
            }
            if (b < '0' || '9' < b) {
                throw new NumberFormatException();
            }
            while (true) {
                if ('0' <= b && b <= '9') {
                    n *= 10;
                    n += b - '0';
                } else if (b == -1 || !isPrintableChar(b)) {
                    return minus ? -n : n;
                } else {
                    throw new NumberFormatException();
                }
                b = readByte();
            }
        }

        public int nextInt() {
            long nl = nextLong();
            if (nl < Integer.MIN_VALUE || nl > Integer.MAX_VALUE) throw new NumberFormatException();
            return (int) nl;
        }

        public long[] nextLongArray(int n) {
            long[] res = new long[n];
            for (int i = 0; i < n; i++) {
                res[i] = nextLong();
            }
            return res;
        }

        public void close() {
            try {
                in.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }

    }
}

import java.util.Scanner;
public class Main {

	public static void main(String[] args) {
		Scanner stdIn = new Scanner(System.in);

		long h = stdIn.nextLong();
		long w = stdIn.nextLong();

		long cnt = 0;
		if(h==1 || w == 1) {
			System.out.println(1);
			return;
		}

		if(h % 2 == 0 && w % 2 == 0) { //h偶数、w偶数
			for(int i = 0; i < h; i++) {
				cnt += w/2;
			}
		}else if(h % 2 == 0 && w % 2 != 0) { //h偶数、w奇数
			for(int i = 0; i < h ; i++) {
				if(i % 2 == 0) {
					cnt += (w+1)/2;
				}else {
					cnt += w/2;
				}
			}
		}else if( h % 2 != 0 && w % 2 == 0) { // h奇数,w偶数
			for(int i = 0; i < h; i++) {
				cnt += w/2;
			}
		}else {                                //h奇数,w奇数
			for(int i = 0; i < h ; i++) {
				if(i % 2 == 0) {
					cnt += (w+1)/2;
				}else {
					cnt += w/2;
				}
			}
		}

		System.out.println(cnt);

	}

