        int s = N/2 + 1;
        while (s > 1 && B[s] != B[s-1]) s--;
        return s;
    }
    private static int getRight(){
        int e = N/2+1;
        while (e < N && B[e] != B[e+1]) e++;
        return e;
    }

    private static boolean valid(int x) {
        for (int i = 1;i <= N;i++)
            B[i] = (A[i] >= x) ? 1 : 0;
        int s = getLeft(),e = getRight(),mid = N/2 + 1;
        if (s == mid || e == mid) return B[mid] == 1;

//        System.err.println(s + " " + e);
        int prv = 0;
        int l = B[s],r = B[e];

        while (s <= e) {
            int h = Math.max(s,e);
            h = Math.min(h,N+1-h) - 1;
            int rem = h - prv;
            prv = h;
            if (rem == 0) break;
            int len = e-s-1;
            if (2*rem <= len) {
                s += rem;
                e -= rem;
            }
            else {
                if (l == r) return l == 1;
                if (mid-s < e-mid) return l == 1;
                else return r==1;
            }
        }
//        System.err.println(": " + s + " " + e);
        if (l == r) return l == 1;
        int pos = mid - s;
        if (l == 0) return pos%2 == 0;
        return pos%2 == 1;
    }
