import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.ArrayList;
import java.util.HashMap;
import java.util.Iterator;


public class Main {

	private BufferedReader reader = new BufferedReader(new InputStreamReader(System.in), 1);

	private String inputData = null;

	public String[] data = null;

	public int num = 0;

	public int count = 0;

	public HashMap<Integer, Integer> humanData = new HashMap<Integer, Integer>();

	public ArrayList<Integer> topId = new ArrayList<Integer>();

	public ArrayList<Integer> topLength = new ArrayList<Integer>();

	public static void main(String[] args) {
		new Main().start();
		System.exit(0);
	}

	public void start() {
		while (true) {
			try {
				this.inputData = reader.readLine();
			} catch (IOException e) {
				e.printStackTrace();
			}

			this.data = this.inputData.split(" ");

			if (this.data.length == 1) {
				if (this.data[0].equals("0")) {
					break;
				}
				try {
					this.num = Integer.parseInt(this.data[0]);
				} catch (Exception e) {
					continue;
				}

			}

			this.humanData.clear();

			for (int i = 0; i < this.num; i++) {
				try {
					this.inputData = reader.readLine();
				} catch (IOException e) {
					e.printStackTrace();
				}
				this.data = this.inputData.split(" ");

				this.humanData.put(Integer.parseInt(data[0]),
						Integer.parseInt(data[1]) + Integer.parseInt(data[2]));

			}

			Iterator<Integer> itId = this.humanData.keySet().iterator();
			int numCount = 0;
			while (itId.hasNext()) {
				int id = itId.next();
				if (numCount == 0) {
					this.topId.add(id);
					this.topLength.add(this.humanData.get(id));
				} else {
					if (this.topLength.get(count) <= this.humanData.get(id)) {
						this.topId.set(this.count, id);
						this.topLength.set(this.count, this.humanData.get(id));
					}
				}
				numCount++;
			}

			this.count++;

		}

		for (int i = 0; i < this.topId.size(); i++) {
			System.out.println(this.topId.get(i) + " " + this.topLength.get(i));
		}

	}

}

import java.util.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int k = findK(n);
        if(k==-1){
            System.out.println("No");
            System.exit(0);
        }
        int[][] res = new int[k][k-1];
        for(int i=0;i<k-1;i++) res[0][i] = i+1;
        int[] index = new int[k];
        for(int i=0;i<k;i++) index[i]=i;
        for(int row=1;row<k;row++){
            int idx = 0;
            for(int j=0;j<row;j++) res[row][idx++] = res[j][index[j]++];
            if(row==k-1) continue;
            res[row][idx++]=res[row-1][k-2]+1;
            while(idx<k-1){
                res[row][idx] = res[row][idx-1]+1;
                idx++;
            }
        }
        System.out.println("Yes");
        System.out.println(k);
        for(int i=0;i<k;i++){
            System.out.print(Integer.toString(k-1)+" ");
            for(int j=0;j<k-1;j++){
                System.out.print(Integer.toString(res[i][j])+" ");
            }
            System.out.println("");
        }
    }
    static int findK(int n){
        int k=1;
        while(k*(k-1)<=2*n){
            if(k*(k-1)==2*n) return k;
            k++;
        }
        return -1;
    }
}

