            } 
            average = average/n; 
            for(int i = 0; i < scores.length; i++){ 
                variance += (scores[i]-average)*(scores[i]-average); 
            }
            variance = variance/n;
            System.out.println(Math.sqrt(variance)); 
            } 
        } 
    }
}

