import java.io.IOException;
import java.io.InputStream;
import java.io.InputStreamReader;
import java.io.OutputStream;
import java.io.PrintWriter;
import java.util.InputMismatchException;
import java.util.StringTokenizer;

public class Main {

	public static void main(String[] args) throws IOException {
		InputStream inputStream = System.in;
		OutputStream outputStream = System.out;
		InputReader in = new InputReader(inputStream);
		PrintWriter out = new PrintWriter(outputStream);
		TaskX solver = new TaskX();
		solver.solve(1, in, out);
		out.close();
	}

	static int INF = 1 << 30;

	static class TaskX {
		public void solve(int testNumber, InputReader in, PrintWriter out) {

			int n = in.nextInt();
			int k = in.nextInt();
			int[] wn = in.nextIntArray(n);

			int left = 0;
			int right = INF;
			int mid = 0;
			while (right > left + 1) {
				mid = (left + right) / 2;
				int count = 1;
				int ca = mid;
				for (int i = 0; i < n; i++) {
					if (ca - wn[i] >= 0) {
						ca -= wn[i];
					} else {
						ca = mid;
						count++;
						if (ca - wn[i] >= 0) {
							ca -= wn[i];
						} else {
							count = INF;
							break;
						}
					}
				}

				// k台以下で積める場合
				if (count <= k){
					right = mid;

				// ｋ台以上必要な場合
				} else if (count > k) {
					left = mid;
				}
			}

			out.println(right);

		}
	}

	static class InputReader {
		BufferedReader in;
		StringTokenizer tok;

		public String nextString() {
			while (!tok.hasMoreTokens()) {
				try {
					tok = new StringTokenizer(in.readLine(), " ");
				} catch (IOException e) {
					throw new InputMismatchException();
				}
			}
			return tok.nextToken();
		}

		public int nextInt() {
			return Integer.parseInt(nextString());
		}

		public long nextLong() {
			return Long.parseLong(nextString());
		}

		public double nextDouble() {
			return Double.parseDouble(nextString());
		}

		public int[] nextIntArray(int n) {
			int[] res = new int[n];
			for (int i = 0; i < n; i++) {
				res[i] = nextInt();
			}
			return res;
		}

		public long[] nextLongArray(int n) {
			long[] res = new long[n];
			for (int i = 0; i < n; i++) {
				res[i] = nextLong();
			}
			return res;
		}

		public InputReader(InputStream inputStream) {
			in = new BufferedReader(new InputStreamReader(inputStream));
			tok = new StringTokenizer("");
		}

	}

}

import java.util.Scanner;

public class Main {

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        int N = sc.nextInt();
        int[] a = new int[N];
        for (int i=0; i<N; i++) {
            a[i] = sc.nextInt();
        }

        int[] ans = solve(N, a);

        for (int i=0; i<N; i++) {
            System.out.println(ans[i]);
        }
    }

    private static int[] solve(int N, int[] a) {
        int[] ans = new int[N];
        int total = Math.abs(a[0]) + Math.abs(a[N-1]);

        for (int i=0; i<N-1; i++) {
            total += Math.abs(a[i+1] - a[i]);
        }

        for (int i=0; i<N; i++) {
            ans[i] = total;

            if (i == 0) {
                if (0 <= a[i] && a[i] <= a[i+1]
                        || a[i+1] <= a[i] && a[i] <= 0) {
//                    System.err.println("ans[" + i + "] : skip");
                    continue;
                }
                ans[i] = total - Math.abs(a[i] - 0) - Math.abs(a[i] - a[i+1]) + Math.abs(a[i+1] - 0);
//                System.err.println("ans[" + i + "] = " + ans[i]);
            } else if (i == N-1) {
                if (0 <= a[i] && a[i] <= a[i-1]
                        || a[i-1] <= a[i] && a[i] <= 0) {
//                    System.err.println("ans[" + i + "] : skip");
                    continue;
                }

                ans[i] = total - Math.abs(a[i] - 0) - Math.abs(a[i] - a[i-1]) + Math.abs(a[i-1] - 0);
//                System.err.println("ans[" + i + "] = " + ans[i]);
            }
            else if (a[i-1] <= a[i] && a[i] <= a[i+1]
                    || a[i+1] <= a[i] && a[i] < a[i-1]) {
//                System.err.println("ans[" + i + "] : skip");
                continue;
            } else {
                ans[i] = total - Math.abs(a[i] - a[i-1]) - Math.abs(a[i] - a[i+1]) + Math.abs(a[i+1] - a[i-1]);
//                System.err.println("ans[" + i + "] = " + ans[i]);
            }
        }

        return ans;
    }
}

