import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStream;
import java.io.InputStreamReader;
import java.util.NoSuchElementException;

public class Main {
    static class FS {
        private final InputStream in = System.in;
        private final byte[] buffer = new byte[1024];
        private int ptr = 0;
        private int buflen = 0;

        private boolean hasNextByte() {
            if (ptr < buflen) {
                return true;
            } else {
                ptr = 0;
                try {
                    buflen = in.read(buffer);
                } catch (IOException e) {
                    e.printStackTrace();
                }
                if (buflen <= 0) {
                    return false;
                }
            }
            return true;
        }

        private int readByte() {
            return hasNextByte() ? buffer[ptr++] : -1;
        }

        private boolean isPrintableChar(int c) {
            return 33 <= c && c <= 126;
        }

        private void skipUnprintable() {
            while (hasNextByte() && !isPrintableChar(buffer[ptr])) ptr++;
        }

        public boolean hasNext() {
            skipUnprintable();
            return hasNextByte();
        }

        public String next() {
            if (!hasNext()) throw new NoSuchElementException();
            StringBuilder sb = new StringBuilder();
            int b = readByte();
            while (isPrintableChar(b)) {
                sb.appendCodePoint(b);
                b = readByte();
            }
            return sb.toString();
        }

        public int nextInt() {
            return (int) nextLong();
        }

        public long nextLong() {
            if (!hasNext()) throw new NoSuchElementException();
            long n = 0;
            boolean minus = false;
            int b = readByte();
            if (b == '-') {
                minus = true;
                b = readByte();
            }
            if (b < '0' || '9' < b) {
                throw new NumberFormatException();
            }
            while (true) {
                if ('0' <= b && b <= '9') {
                    n *= 10;
                    n += b - '0';
                } else if (b == -1 || !isPrintableChar(b)) {
                    return minus ? -n : n;
                } else {
                    throw new NumberFormatException();
                }
                b = readByte();
            }
        }
    }

    static int[] np;
    static int n;

    public static void main(String[] args) throws Exception {
        FS fs = new FS();
//        BufferedReader bf = new BufferedReader(new InputStreamReader(System.in));
//        String s = bf.readLine();
//        int n = Integer.parseInt(s.substring(0, s.indexOf(' ')));//n packages
//        int k = Integer.parseInt(s.substring(s.indexOf(' '), s.length()));//k trucks
        n = fs.nextInt();
        int k = fs.nextInt();


        np = new int[n];
        int max = 0;
        for (int i = 0; i < n; i++) {

            np[i] = fs.nextInt();
            max = max < np[i] ? np[i] : max;
        }
        int p = 0;
        int resids = 0;

        int maxInt = 10000 *100000;
        int minInt = max-1;
        int mid = (maxInt + minInt) / 2;
        while (maxInt - minInt > 1) {
            int v = checkN(mid);
            if (v > k) minInt = mid;
            else maxInt = mid;

            mid = (maxInt + minInt) / 2;
        }
        System.out.println(maxInt);
    }

    private static int checkN(int p) {
        int resids = p;
        int ink = 1;//当前已用卡车
        for (int i = 0; i < n; i++) {
            int j = np[i];
            if (resids >= j) {
                resids -= j;
            } else {
                ink++;
                resids = p;
                i--;
            }
        }
        return ink;
    }
}

import java.io.IOException;
import java.io.InputStream;
import java.io.PrintWriter;
import java.util.*;

public class Main {
    public static void main(String[] args) {
        PrintWriter out = new PrintWriter(System.out);
        InputStreamScanner in = new InputStreamScanner(System.in);
        new Main().solve(in, out);
        out.flush();
    }

    private void solve(InputStreamScanner in, PrintWriter out) {
        String s = in.next();

        long t = 0;
        for (int i = 0; i < 1 << (s.length() - 1); i++) {
            int p = 0;
            for (int j = 0; j < s.length() - 1; j++) {
                if ((1 & i >> j) == 1) {
                    t += Long.parseLong(s.substring(p, j + 1));
                    p = j + 1;
                }
            }
            t += Long.parseLong(s.substring(p));
        }

        out.println(t);
    }

    static class InputStreamScanner {

        private InputStream in;

        private byte[] buf = new byte[1024];
        private int len = 0;
        private int off = 0;

        InputStreamScanner(InputStream in)	{
            this.in = in;
        }

        String next() {
            StringBuilder sb = new StringBuilder();
            for (int b = skip(); !isSpace(b);){
                sb.appendCodePoint(b);
                b = read();
            }
            return sb.toString();
        }

        int nextInt() {
            return Integer.parseInt(next());
        }

        long nextLong() {
            return Long.parseLong(next());
        }

        double nextDouble() {
            return Double.parseDouble(next());
        }

        char nextChar() {
            return (char)skip();
        }

        int skip() {
            for (int b; (b = read()) != -1;) {
                if (!isSpace(b)) {
                    return b;
                }
            }
            return -1;
        }

        private boolean isSpace(int c) {
            return c < 33 || c > 126;
        }

        private int read() {
            if (len == -1) {
                throw new InputMismatchException("End of Input");
            }
            if (off >= len){
                off = 0;
                try {
                    len = in.read(buf);
                } catch (IOException e) {
                    throw new InputMismatchException(e.getMessage());
                }
                if (len <= 0) {
                    return -1;
                }
            }
            return buf[off++];
        }
    }
}

import java.util.ArrayList;
import java.util.PriorityQueue;
import java.util.Scanner;

/**
 * Created by shoya on 2017/04/12.
 */
public class Main {
    public static void main(String... args) {
        Scanner sc = new Scanner(System.in);
        int V = sc.nextInt();
        int E = sc.nextInt();
        int r = sc.nextInt();
        DijkstraClass dijkstra = new DijkstraClass(V, E, r);
        for (int i = 0; i < E; i++) {
            int s = sc.nextInt();
            int d = sc.nextInt();
            int c = sc.nextInt();
            dijkstra.addDistande(s, d, c);
        }
        int[] dist = dijkstra.dikstra();
        for (int i = 0; i < dist.length; i++){
            if (dist[i] == 1 << 30)
                System.out.println("INF");
            else
                System.out.println(dist[i]);
        }
        return;
    }


    private static class DijkstraClass {
        class edge{
            int dest, cost;
            edge(int dest, int cost){
                this.dest = dest;
                this.cost = cost;
            }
        }

        private final int INF = 1 << 30;
        private int S;
        private int V;
        private ArrayList<ArrayList<edge>> edgesOfNodes = new ArrayList<ArrayList<edge>>();

        DijkstraClass(int V, int E, int Start){
            this.S = Start;
            this.V = V;
            for (int i = 0; i < V; i++)
                edgesOfNodes.add(new ArrayList<edge>());
        }

        void addDistande(int source, int dest, int cost){
            edgesOfNodes.get(source).add(new edge(dest, cost));
        }


        int[] dikstra(){
            class costAndNode implements Comparable {
                int cost, node;
                costAndNode(int cost, int node){
                    this.cost = cost;
                    this.node = node;
                }
                @Override
                public int compareTo(Object o) {
                    return this.cost - ((costAndNode) o).cost;
                }
            }

            PriorityQueue<costAndNode> pque = new PriorityQueue<costAndNode>();
            int[] distances = new int[V];
            for (int i = 0; i < V; i++)
                distances[i] = INF;
            distances[S] = 0;

            pque.add(new costAndNode(0, S));
            while (!pque.isEmpty()){
                costAndNode item = pque.poll();
                if (distances[item.node] < item.cost)
                    continue;

                //System.out.printf("Focusing on Node %d\n", item.node);
                //for (int i = 0; i < V; i++)
                //    System.out.printf("%d ", distances[i]);
                //System.out.println();

                for (edge e : edgesOfNodes.get(item.node)) {
                    //System.out.printf("Node %d -> %d, Cost %d -> %d\n",
                    //        item.node, e.dest, distances[e.dest],
                    //        distances[item.node] + e.cost);
                    if (distances[e.dest] > distances[item.node] + e.cost) {
                        distances[e.dest] = distances[item.node] + e.cost;
                        pque.add(new costAndNode(distances[e.dest], e.dest));
                    }
                }
            }

            return distances;
        }

    }
}

