    static class TaskC {
        public void solve(int testNumber, MyInput in, PrintWriter out) {
            int n = in.nextInt();
            int[] d = in.nextIntArray(n);
            int ans = 0;
            LOOP:
            for (int i = 24; i >= 0; i--) {
                boolean[] vis = new boolean[n];
                int[] time = new int[n + 1];
                for (int j = 0; j < n; j++) {
                    int idx = -1, t = 1 << 29;
                    for (int k = 0; k < n; k++) {
                        for (int r = 0; r < 2; r++) {
                            int cur = r == 0 ? d[k] : 24 - d[k];
                            if (!vis[k] && cur >= time[j] + i && (idx == -1 || cur < t)) {
                                idx = k;
                                t = cur;
                                break;
                            }
                        }
                    }
                    if (idx == -1) continue LOOP;
                    vis[idx] = true;
                    time[j + 1] = t;
                }
                if ((24 - time[n]) % 24 >= i) {
                    ans = i;
                    break;
                }
            }
            out.println(ans);
        }

    }

