	static class TaskX {
		public void solve(int testNumber, InputReader in, PrintWriter out) {

			int n = in.nextInt();
			long[] t = in.nextLongArray(n), a = in.nextLongArray(n);

			long[] tCalc = new long[n];
			Arrays.fill(tCalc, -1);

			long[] aCalc = new long[n];
			Arrays.fill(aCalc, -1);

			long[] tDef = new long[n];
			long[] aDef = new long[n];

			long tMax = -1, aMax = -1;
			for (int i = 0; i < n; i++) {
				if (tMax < t[i]) {
					tMax = t[i];
					tDef[i] = t[i];
				} else {
					tCalc[i] = tMax;
				}
			}
			for (int i = n-1; i >= 0; i--) {
				if (aMax < a[i]) {
					aMax = a[i];
					aDef[i] = a[i];
				} else {
					aCalc[i] = aMax;
				}
			}

			for (int i = 0; i < n; i++) {
				if (aCalc[i] == -1 && tCalc[i] == -1) {
					if (aDef[i] != tDef[i]) {
						out.println(0);
						return;
					}
				} else if (aCalc[i] == -1) {
					if (aDef[i] > tCalc[i]) {
						out.println(0);
						return;
					}
				} else if (tCalc[i] == -1) {
					if (tDef[i] > aCalc[i]) {
						out.println(0);
						return;
					}
				}
			}

			long ans = 1;
			for (int i = 0; i < n; i++) {
				if (tCalc[i] == -1 || aCalc[i] == -1) {
					continue;
				}
				ans = ans * min(aCalc[i],tCalc[i]) % modP;
			}
			out.println(ans);
		}
	}

