    static int[] h;


    public static void main(String[] args)
    {
        N = sc.nextInt();
        A = sc.nextInt();
        B = sc.nextInt();
        h = sc.nextIntArray(N);
        int ok = (int) Math.ceil(max(h) * 1.0 / B);
        int ng = 0;
        System.out.println(mgr(ok,ng));

    }

    static int mgr(int ok, int ng)
    {
        while (Math.abs(ok - ng) > 1)
        {
            int mid = (ok + ng) / 2;
            if (solve(mid))
            {
                ok = mid;
            }
            else
            {
                ng = mid;
            }
        }
        return ok;
    }

    static boolean solve(long K)
    {
        long k = 0;
        for (int i = 0; i < N; i++)
        {
            long hp = h[i];
            hp -= K * B;
            if (hp <= 0) continue;
            k += (long) Math.ceil(hp * 1.0 / (A - B));
        }
        if (K < k) return false;
        else return true;
    }

    public static boolean isOutofIndex(int x, int y)
    {
